from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
from typing import List, Dict, Optional
from enum import Enum
import random
import json
import os
from fastapi.middleware.cors import CORSMiddleware

# --- Constants ---
DATA_FILE = "data.json"

# --- Enums & Models ---
class ConstraintType(str, Enum):
    TEACHER_UNAVAILABLE = "teacher_unavailable"
    SUBJECT_FIXED = "subject_fixed"

class Teacher(BaseModel): id: int; name: str
class Subject(BaseModel): id: int; name: str; weekly_hours: int
class Grade(BaseModel): id: int; name: str
class TeacherSubjectLink(BaseModel): id: int; teacher_id: int; subject_id: int
class TimetableSlot(BaseModel): subject_id: int; teacher_id: int
class Constraint(BaseModel): id: int; type: ConstraintType; teacher_id: Optional[int] = None; subject_id: Optional[int] = None; grade_id: Optional[int] = None; day: int; period: int
class Timetable(BaseModel): timetable: Dict[str, List[List[Optional[TimetableSlot]]]]

# --- Create Models ---
class TeacherCreate(BaseModel): name: str
class SubjectCreate(BaseModel): name: str; weekly_hours: int
class GradeCreate(BaseModel): name: str
class ConstraintCreate(BaseModel): type: ConstraintType; teacher_id: Optional[int] = None; subject_id: Optional[int] = None; grade_id: Optional[int] = None; day: int; period: int
class TeacherSubjectLinkCreate(BaseModel): teacher_id: int; subject_id: int

# --- App & DB Setup ---
app = FastAPI()
app.add_middleware(CORSMiddleware, allow_origins=["http://localhost:3000"], allow_credentials=True, allow_methods=["*"], allow_headers=["*"])

db_teachers: List[Teacher] = []
db_subjects: List[Subject] = []
db_grades: List[Grade] = []
db_constraints: List[Constraint] = []
db_teacher_subject_links: List[TeacherSubjectLink] = []

# --- Data Persistence ---
def save_data():
    all_data = {
        "teachers": [t.dict() for t in db_teachers],
        "subjects": [s.dict() for s in db_subjects],
        "grades": [g.dict() for g in db_grades],
        "constraints": [c.dict() for c in db_constraints],
        "teacher_subject_links": [l.dict() for l in db_teacher_subject_links],
    }
    with open(DATA_FILE, "w", encoding="utf-8") as f: json.dump(all_data, f, ensure_ascii=False, indent=4)

def load_data():
    if not os.path.exists(DATA_FILE): return
    with open(DATA_FILE, "r", encoding="utf-8") as f:
        all_data = json.load(f)
        db_teachers.extend([Teacher(**t) for t in all_data.get("teachers", [])])
        db_subjects.extend([Subject(**s) for s in all_data.get("subjects", [])])
        db_grades.extend([Grade(**g) for g in all_data.get("grades", [])])
        db_constraints.extend([Constraint(**c) for c in all_data.get("constraints", [])])
        db_teacher_subject_links.extend([TeacherSubjectLink(**l) for l in all_data.get("teacher_subject_links", [])])

# --- APIs ---
@app.get("/teachers")
def get_teachers(): return db_teachers
@app.post("/teachers")
def create_teacher(teacher: TeacherCreate): 
    new = Teacher(id=max([t.id for t in db_teachers] or [0]) + 1, name=teacher.name)
    db_teachers.append(new); save_data(); return new

@app.get("/subjects")
def get_subjects(): return db_subjects
@app.post("/subjects")
def create_subject(subject: SubjectCreate):
    new = Subject(id=max([s.id for s in db_subjects] or [0]) + 1, **subject.dict())
    db_subjects.append(new); save_data(); return new

@app.get("/grades")
def get_grades(): return db_grades
@app.post("/grades")
def create_grade(grade: GradeCreate):
    new = Grade(id=max([g.id for g in db_grades] or [0]) + 1, name=grade.name)
    db_grades.append(new); save_data(); return new

@app.get("/teacher-subject-links")
def get_links(): return db_teacher_subject_links
@app.post("/teacher-subject-links")
def create_link(link: TeacherSubjectLinkCreate):
    new = TeacherSubjectLink(id=max([l.id for l in db_teacher_subject_links] or [0]) + 1, **link.dict())
    db_teacher_subject_links.append(new); save_data(); return new
@app.delete("/teacher-subject-links/{link_id}")
def delete_link(link_id: int): 
    global db_teacher_subject_links; db_teacher_subject_links = [l for l in db_teacher_subject_links if l.id != link_id]; save_data(); return {"ok": True}

@app.get("/constraints")
def get_constraints(): return db_constraints
@app.post("/constraints")
def create_constraint(constraint: ConstraintCreate):
    new = Constraint(id=max([c.id for c in db_constraints] or [0]) + 1, **constraint.dict())
    db_constraints.append(new); save_data(); return new
@app.delete("/constraints/{constraint_id}")
def delete_constraint(constraint_id: int):
    global db_constraints; db_constraints = [c for c in db_constraints if c.id != constraint_id]; save_data(); return {"ok": True}

